      const weatherNextDay = this.props.weatherOnDate;
      console.log(weatherNextDay.weather[0].main.toLowerCase());

      return (
          <section>
              {moment(weatherNextDay).add(this.props.numberOfDay, 'days').format('dddd').substr(0, 3).toUpperCase()}
              <div>
              <span><Icon size="small" weatherType={weatherToday.weather[0].main.toLowerCase()} /></span>
