import React from 'react';
import Icon from '../Icon/index';
import moment from 'moment';

export default class WeatherForecast extends React.Component {

    render() {
      const weatherToday = this.props.weatherOnDate;
      const temperature = weatherToday.temp.day;

      return (
          <section>
              {moment(weatherToday).add(this.props.numberOfDay, 'days').format('dddd').substr(0, 3).toUpperCase()}
              <div>
              <span><Icon size="small" weatherType={weatherToday.weather[0].main.toLowerCase()} /></span>
              <span>{Math.round(temperature)}&#176;C</span>
            </div>
          </section>
        );
    }  
  }
